void ltrim(char <em>s)
{
 char *p;
 p = s;
 while(</em>p == ' ' || <em>p == 't'){</em>p++;}
 strcpy(s,p);
}
void rtrim(char *s)
{
 int i;
 if (s == NULL)
   return;
 i = strlen(s)-1;
 while((s[i] == ' ' || s[i] == 't') &amp;&amp; i >= 0){i--;};
 s[i+1] = '�';
}
void trim(char *s){
    ltrim(s);
    rtrim(s);
}

